import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';



export function Input(props:any){
    const [textwhile,settextwhile] = useState("");
     const [text,settext] = useState("");

    function handleChange(e:any){
            settext(e.target.value)
            settextwhile("")
        }
    function handleKeyPress(e:any){
        if (e.key === "Enter"){
            props.added(text);
          }
    }

    return(
        <div className="input-div" onKeyPress={handleKeyPress}>
        <input className="input" value={textwhile} type="text" onChange={handleChange} placeholder="todo"/>
        <Button className="addbtn" variant="primary" size="sm" onClick={() => props.added(text)} >Add</Button>
        </div>
    )
}
